package basicprogram;

public class Increment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k = 2;
	
		

		System.out.println(k++ - ++k + k-- - k++ + ++k -k-- + --k + k+ k--- k + --k+ k++);
		//                  2     3     3     2    4    4       3    3  2    2        1
		
		
	
	}

}
